mapper.select:
SELECT Row_Number()over(ORDER BY a.id DESC) as id, d.Name as lineName, c.Name as stationName, b.Name as deviceName, a.name, a.duration, a.playCount FROM Broadcast a LEFT JOIN Device b ON a.DeviceID= b.ID LEFT JOIN Station c ON a.StationID= c.ID LEFT JOIN Line d ON a.LineID= d.ID WHERE convert(char(10),PlayDate,20) between #{startDate} and #{endDate} AND a.Type=#{type}
AND a.LineID =#{lineID}
AND a.StationID =#{stationID}
AND a.DeviceID =#{deviceID}

SELECT TOP (#{pageSize}) RowNumber as id,b.Name as deviceName,c.Name as stationName,d.Name as lineName, a.Name,a.Duration,PlayCount FROM ( SELECT ROW_NUMBER () OVER ( ORDER BY id DESC) AS RowNumber,* FROM Broadcast WHERE convert(char(10),PlayDate,20) between #{startDate} and #{endDate} AND Type=#{type}
AND LineID =#{lineID}
AND StationID =#{stationID}
AND DeviceID =#{deviceID}
) AS A LEFT JOIN Device b on a.DeviceID=b.ID LEFT JOIN Station c on a.StationID=c.ID LEFT JOIN Line d on a.LineID=d.id WHERE RowNumber > #{pageSize} * ( #{pageNum}-1)

SELECT
    count(*)
FROM
    Broadcast
WHERE
convert(char(10),PlayDate,20) between #{startDate} and #{endDate}
AND Type=#{type}

    AND LineID =#{lineID}


    AND StationID =#{stationID}


    AND DeviceID =#{deviceID}
