mapper.select:
SELECT Row_Number()over(ORDER BY a.id DESC) as id, d.Name AS lineName, c.Name AS stationName, b.Name AS deviceName, a.Ctrl_Type, a.Ctrl_Time, a.Ctrl_Result FROM Ctrl_Event a LEFT JOIN Device b ON a.DeviceID= b.ID LEFT JOIN Station c ON a.StationID= c.ID LEFT JOIN Line d ON a.LineID= d.id WHERE convert(char(10),a.Ctrl_Time,20) between #{startDate} and #{endDate}
AND a.LineID =#{lineID}
AND a.StationID =#{stationID}
AND a.DeviceID =#{deviceID}
AND b.Type=#{type}

SELECT TOP (#{pageSize}) RowNumber as id,b.Name as lineName,c.Name as stationName,d.Name as deviceName, a.Ctrl_Type,a.Ctrl_Time,a.Ctrl_Result FROM ( SELECT ROW_NUMBER () OVER ( ORDER BY id ASC) AS RowNumber,* FROM Ctrl_Event WHERE convert(char(10),Ctrl_Time,20) between #{startDate} and #{endDate}
AND LineID =#{lineID}
AND StationID =#{stationID}
AND DeviceID =#{deviceID}
) AS A LEFT JOIN Device b on a.DeviceID=b.ID LEFT JOIN Station c on a.StationID=c.ID LEFT JOIN Line d on a.LineID=d.id WHERE
b.Type=#{type} AND
RowNumber > #{pageSize} * ( #{pageNum}-1)

SELECT
    COUNT(*)
FROM
    Ctrl_Event a
    LEFT JOIN Device b on a.DeviceID=b.ID
WHERE
convert(char(10),a.Ctrl_Time,20) between #{startDate} and #{endDate}

    AND a.LineID =#{lineID}


    AND a.StationID =#{stationID}


    AND a.DeviceID =#{deviceID}


    AND b.Type=#{type}
