mapper.select:
SELECT Row_Number()over(ORDER BY a.id DESC) as id, d.Name AS lineName, c.Name AS stationName, b.Name AS deviceName, a.Accendant, a.Tel, a.Note, a.repairPeople, a.repairTel, a.Scheme, a.StartTime, a.EndTime, a.RepairResult FROM DevRepair a LEFT JOIN Device b ON a.DeviceID= b.ID LEFT JOIN Station c ON a.StationID= c.ID LEFT JOIN Line d ON a.LineID= d.id WHERE convert(char(10),a.EndTime,20) between #{startTime} and #{endTime}
AND a.LineID = #{lineID}
AND a.StationID = #{stationID}
AND a.DeviceID = #{deviceID}
AND b.Type=#{type}

SELECT TOP (#{pageSize}) RowNumber as id,b.Name as deviceName,c.Name as stationName,d.Name as lineName,a.Accendant, a.Tel,a.Note,a.Scheme,a.RepairPeople,a.repairTel,a.StartTime,a.EndTime,a.RepairResult FROM ( SELECT ROW_NUMBER () OVER ( ORDER BY id DESC) AS RowNumber,* FROM DevRepair WHERE convert(char(10),StartTime,20) between #{devRepair.startTime} and #{devRepair.endTime}
AND LineID = #{devRepair.lineID}
AND StationID = #{devRepair.stationID}
AND DeviceID = #{devRepair.deviceID}
) AS A LEFT JOIN Device b on a.DeviceID=b.ID LEFT JOIN Station c on a.StationID=c.ID LEFT JOIN Line d on a.LineID=d.id WHERE
b.Type=#{devRepair.type} AND
RowNumber > #{pageSize} * ( #{pageNum}-1)

        SELECT
            count(*)
        FROM
            DevRepair a
            LEFT JOIN Device b on a.DeviceID=b.ID
		WHERE
