mapper.select:
SELECT Row_Number()over(ORDER BY a.id DESC) as id, d.Name AS lineName, c.Name AS stationName, b.Name AS deviceName, a.Accendant, a.Tel, a.Scheme, a.StartTime, a.EndTime, a.UpkeepState FROM DevUpkeep a LEFT JOIN Device b ON a.DeviceID= b.ID LEFT JOIN Station c ON a.StationID= c.ID LEFT JOIN Line d ON a.LineID= d.id WHERE a.EndTime IS NOT NULL AND convert(char(10),a.EndTime,20) between #{startTime} and #{endTime}
AND a.LineID =#{lineID}
AND a.StationID =#{stationID}
AND a.DeviceID =#{deviceID}
AND b.Type=#{type}

SELECT TOP (#{pageSize}) RowNumber as id,d.Name as lineName,c.Name as stationName,b.Name as deviceName, a.Accendant,a.Tel,a.Scheme,a.StartTime,a.EndTime,a.UpkeepState FROM ( SELECT ROW_NUMBER () OVER ( ORDER BY id DESC) AS RowNumber,* FROM DevUpkeep WHERE EndTime IS NOT NULL AND convert(char(10),StartTime,20) between #{devUpkeep.startTime} and #{devUpkeep.endTime}
AND LineID =#{devUpkeep.lineID}
AND StationID =#{devUpkeep.stationID}
AND DeviceID =#{devUpkeep.deviceID}
) AS A LEFT JOIN Device b on a.DeviceID=b.ID LEFT JOIN Station c on a.StationID=c.ID LEFT JOIN Line d on a.LineID=d.id WHERE
b.Type=#{devUpkeep.type} AND
RowNumber > #{pageSize} * ( #{pageNum}-1)

SELECT
    count(*)
FROM
    DevUpkeep a
    LEFT JOIN Device b on a.DeviceID=b.ID
WHERE
EndTime IS NOT NULL
AND convert(char(10),a.StartTime,20) between #{startTime} and #{endTime}

    AND a.LineID =#{lineID}


    AND a.StationID =#{stationID}


    AND a.DeviceID =#{deviceID}


    AND b.Type=#{type}

mapper.update:
UPDATE DevUpkeep
SET EndTime =#{endTime},
UpkeepState =#{upkeepState}
WHERE
    id =#{id}
