mapper.select:
SELECT
    *
FROM
    Device a
LEFT JOIN Station b on a.StationID=b.ID

    b.IsTrain=0

       AND a.id=#{id}


       AND a.LineID = #{lineID}


       AND a.StationID = #{stationID}

SELECT
    *
FROM
    Device a
    LEFT JOIN Station b ON a.StationID= b.ID
WHERE
    b.IsTrain= 0

      AND a.LineID=#{lineID}


      AND a.StationID=#{stationID}


        AND a.Type =#{type}

SELECT TOP (#{pageSize}) a.*,b.Name as lineName,c.Name as stationName,d.Name as devicePosName FROM ( SELECT ROW_NUMBER () OVER ( ORDER BY id DESC) AS RowNumber,* FROM Device
LineID=#{lineID}
and StationID=#{stationID}
and Type=#{type}
and id=#{id}
) AS a LEFT JOIN Line b on a.lineID=b.ID LEFT JOIN Station c on a.StationID=c.ID LEFT JOIN Device_Pos d on a.DevicePosID=d.ID WHERE c.IsTrain=0 and RowNumber > #{pageSize} * ( #{pageNum}-1)

SELECT COUNT
( * )
FROM
Device a
LEFT JOIN Station b ON a.StationID= b.id
WHERE
b.IsTrain= 0

    and a.LineID=#{lineID}


    and a.StationID=#{stationID}


    and a.Type=#{type}


    and a.id=#{id}

SELECT COUNT
    (*)
FROM
    Device
WHERE
   (DeviceID = #{deviceID} OR Name = #{name})
    AND LineID = #{lineID}
    AND StationID = #{stationID}

    and id !=#{id}

mapper.update:
UPDATE Device SET DeviceID =#{deviceID}, Name =#{name}, Des =#{des}, Type =#{type}, DevicePosID =#{devicePosID}, IP =#{ip}, Mac =#{mac}, StationID =#{stationID}, LineID =#{lineID}, Param =#{param}, Resolution =#{resolution}
,IsBackups =#{isBackups}
WHERE id =#{id}
