mapper.select:
SELECT TOP (#{pageSize}) * FROM ( SELECT ROW_NUMBER () OVER ( ORDER BY id DESC) AS RowNumber,* FROM Msg_Fodder WHERE Type=#{type}
AND State=#{state}
) AS A WHERE RowNumber > #{pageSize} * ( #{pageNum}-1)

mapper.update:
UPDATE Msg_Fodder SET State =#{state}
WHERE
    ID =#{id}

UPDATE Msg_Fodder SET Title =#{title},Content_CN =#{contentCN},Content_EN=#{contentEN}
WHERE
    ID =#{id}
