mapper.select:
SELECT Row_Number()over(ORDER BY a.id ASC) as id, h.Name as lineName,g.Name as stationName,f.Name as deviceName, e.Name as fodderName,b.Start_Date,b.End_Date,a.Editor FROM Playlist_Client a LEFT JOIN Play_List b on a.PlaylistID=b.ID LEFT JOIN PlayList_Style c on b.ID=c.PlaylistID LEFT JOIN Style_Content d on c.StyleID=d.StyleID LEFT JOIN Fodder e on d.MaterialID=e.ID LEFT JOIN Device f on a.ClientID=f.ID LEFT JOIN Station g on f.StationID=g.ID LEFT JOIN Line h on f.LineID=h.ID WHERE convert(char(10),a.AuditTime,20) between #{startDate} and #{endDate} AND(d.fileproterty='image' or d.fileproterty='video')
and a.ClientID IN ( SELECT id FROM Device WHERE
LineID = #{lineID}
AND StationID = #{stationID}
AND ID = #{deviceID}
)

SELECT TOP (#{pageSize}) * FROM ( SELECT ROW_NUMBER () OVER ( ORDER BY a.id ASC) AS id, h.Name as lineName,g.Name as stationName,f.Name as deviceName, e.Name as fodderName,b.Start_Date,b.End_Date,a.Editor FROM Playlist_Client a LEFT JOIN Play_List b on a.PlaylistID=b.ID LEFT JOIN PlayList_Style c on b.ID=c.PlaylistID LEFT JOIN Style_Content d on c.StyleID=d.StyleID LEFT JOIN Fodder e on d.MaterialID=e.ID LEFT JOIN Device f on a.ClientID=f.ID LEFT JOIN Station g on f.StationID=g.ID LEFT JOIN Line h on f.LineID=h.ID where convert(char(10),a.AuditTime,20) between #{startDate} and #{endDate} AND(d.fileproterty='image' or d.fileproterty='video')
and a.ClientID IN ( SELECT id FROM Device WHERE
LineID = #{lineID}
AND StationID = #{stationID}
AND ID = #{deviceID}
)
) AS a WHERE id > #{pageSize} * ( #{pageNum}-1)

select count(*) from Playlist_Client a LEFT JOIN Play_List b on a.PlaylistID=b.ID LEFT JOIN PlayList_Style c on b.ID=c.PlaylistID LEFT JOIN Style_Content d on c.StyleID=d.StyleID LEFT JOIN Fodder e on d.MaterialID=e.ID LEFT JOIN Device f on a.ClientID=f.ID LEFT JOIN Station g on f.StationID=g.ID LEFT JOIN Line h on f.LineID=h.ID WHERE convert(char(10),a.AuditTime,20) between #{startDate} and #{endDate} AND(d.fileproterty='image' or d.fileproterty='video')
and a.ClientID IN ( SELECT id FROM Device WHERE
LineID = #{lineID}
AND StationID = #{stationID}
AND ID = #{deviceID}
)

SELECT
    *
FROM
    Playlist_Client
WHERE
    PlaylistID = #{ playListID}
    AND AuditTime = #{ auditTime}
    AND ClientID != #{ clientID}

mapper.update:
UPDATE Playlist_Client SET Sequence =#{sequence}

    ,AuditTime=#{auditTime}


    ,Editor=#{editor}

WHERE
    ID =#{id}
